import React, { Component } from 'react';
import { Link } from '@reach/router';
import * as api from '../Utils/fetchData';
import Loader from './Loader';


class AllTopics extends Component {
  state = {
    isLoading: true,
    topics: []
  };
  render() {
    const { isLoading, topics } = this.state;
    if (isLoading)
      return <Loader type="Bars" color="#somecolor" height={80} width={80} />;
    return (
      <div>
        <ul>
          {topics.map(({ slug, description }) => {
            return (
              <li key={slug}>
                <p>Topic: {slug} </p>
                <p>{description}</p>
                <Link to={`/topics/${slug}`}>View Articles</Link>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
  componentDidMount = () => {
    this.fetchTopics();
  };
  componentDidUpdate = (prevProps, prevState) => {
    if (prevProps.description !== this.props.description) {
      this.addTopicCall();
    }
  }
  fetchTopics = () => {
    api.fetchTopics().then(topics => {
      this.setState(
        {
          topics,
          isLoading: false
        },
        () => console.log(this.state)
      );
    });
  };

  addTopicCall = () => {
    const { slug, description } = this.props.addTopicCall;
    return fetch(`https://lloyd-news.herokuapp.com/api/topics`, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        slug: slug,
        description: description
      })
    }).then(res => res.json())
      .then(({ topic }) => {
          this.setState(prevState => {
            return { topics: [...topic, ...prevState.topics] };
          });
      })


  }

}

export default AllTopics;
