import axios from 'axios';

const BASE_URL = 'https://lloyd-news.herokuapp.com/api';

export const fetchAllArticles = async(p = 1) => {
    const { data } = await axios.get(`${BASE_URL}/articles?p=${p}`);
    return data.articles;
}

export const fetchArticle = async (id) => {
    const { data } = await axios.get(`${BASE_URL}/articles/${id}`);
    return data;
}

export const fetchArticlesByTopic = async (topic, p = 1) => {
    const { data } = await axios.get(`${BASE_URL}/topics/${topic}/articles?p=${p}`)
    return data.articles;
}

export const fetchComments = async (id) => {
    const { data } = await axios.get(`${BASE_URL}/articles/${id}/comments`);
    return data.comments;
}

export const deleteData = async (commentid, articleid) => {
    await axios.delete(`https://lloyd-news.herokuapp.com/api/articles/${String(articleid)}/comments/${commentid}`);

}

export const fetchTopics = async () => {
    const { data } = await axios.get(`${BASE_URL}/topics`);
    return data.topics;
}

export const fetchArticlesSort = async(sort_by='created_at', p=1) => {
    const { data } = await axios.get(`${BASE_URL}/articles?sort_by=${sort_by}&p=${p}`);
    return data.articles;
}

export const fetchUser = async(user) => {
    const { data } = await axios.get(`${BASE_URL}/users/${user}`);
    return data.user;
}


export const fetchArticles = async (slug, sort_by='created_at', p=1) => {

    const request = slug ? 
        `${BASE_URL}/topics/${slug}?sort_by=${sort_by}&p=${p}` :
     `${BASE_URL}/topics/${slug}?sort_by=${sort_by}&p=${p}`;

    const { data } = await axios.get(request);
    return data.articles;
}
