import React, { Component } from 'react';
import { Link } from '@reach/router';
import Comments from './Comments';
import * as api from '../Utils/fetchData';
import Loader from './Loader';
import formatDate from '../Utils/utilFunctions';

class Article extends Component {

    state = {
        toggleComments: false,
        article: [],
        isLoading: true,
        comments: []
    }
    render() {

        const { isLoading } = this.state;
        const { body, title, author, created_at } = this.state.article
        if (isLoading) return <Loader />;

        const {articleid } = this.props;

        return <div>
            <h2>{title}</h2>
            <p>{body}</p>
            <p>by {author}</p>
            <p>published: {formatDate(created_at)}</p>
            <button type="submit">
              <Link to="/articles">Back</Link>
            </button>
            <button onClick={this.handleToggleComments} type="submit">
              comments
            </button>
            {this.state.toggleComments && 
                <Comments 
                    comments={this.state.comments} 
                    getComment={this.getComment} 
                    articleid={articleid}
                />}
          </div>;
    }

    componentDidMount() {
       this.handleFetchArticle();  
       this.handleFetchComments();
    }

    componentDidUpdate = (prevProps, prevState) => {
        if (prevState.comments.length !== this.state.comments.length) {
            this.handleUpdateComments();
        }

    }

    handleUpdateComments = () => {
        const newComment = this.getComment();
        this.setState((prevState) => ({
            comments: [...prevState.comments, newComment]
        }))
        console.log(newComment)
    }
    getComment = (comment) => {
        return comment;

    }

    handleToggleComments = () => {
        this.setState((prevState) => ({
            toggleComments: !prevState.toggleComments
        }))
    }
    handleFetchArticle = () => {
        const { articleid } = this.props;
        api.fetchArticle(articleid)
        .then(article => {
            const singleArticle = article.article
            this.setState({
                article: singleArticle,
                isLoading: false,
            })
        })
    }
    handleFetchComments = () => {
        const { articleid } = this.props;
        api.fetchComments(articleid)
        .then(comments => {
            this.setState({
                comments
            })
        })
    }
}

export default Article;
