import React, { Component } from 'react';
import * as api from '../Utils/fetchData';

class Users extends Component {
  State = {
    users: []
  }
  render() {
    const { users } = this.state;
    return (
      <div>
        <h1>Users</h1>
        <ul>
        {users.map(user => {
          return <li>{user.username}</li>
        })}
        </ul>
      </div>
    );
  }
  componentDidMount = () => {
    this.fetchUsers();
  }
  fetchUsers = () => {
    api.fetchAllUsers().then(users => {
      this.setState({
        users
      })
    })
  }
}

export default Users;
