import React, { Component } from 'react';
import * as api from '../Utils/fetchData';

class Users extends Component {
  _isMounted = false;
  state = {
    users: [
      {
        "username": "tickle122",
        "avatar_url": "https://avataaars.io/?avatarStyle=Circle&topType=ShortHairDreads02&accessoriesType=Wayfarers&hairColor=Black&facialHairType=Blank&facialHairColor=Blonde&clotheType=BlazerShirt&clotheColor=PastelOrange&graphicType=Diamond&eyeType=Happy&eyebrowType=SadConcerned&mouthType=Concerned&skinColor=Pale",
        "name": "Tom Tickle"
      },
      {
        "username": "grumpy19",
