import React, { Component } from 'react';
import { Link } from '@reach/router';
import Comments from './Comments';
import * as api from '../Utils/fetchData';
import Loader from './Loader';

class Article extends Component {

    state = {
        toggleComments: false,
        article: [],
        isLoading: true,
        body: ''
    }
    render() {

        const { isLoading, article } = this.state;
        if (isLoading) return <Loader />;
        console.log(this.state, 'state before call')
        return <div>
            <p>This is the article page</p>
            {console.log(article, 'article here')}
            <button type="submit">
              <Link to="/articles">Back</Link>
            </button>
            <button onClick={this.handleToggleComments} type="submit">
              comments
            </button>
            {this.state.toggleComments && <Comments />}
          </div>;
    }
    // componentDidMount = () => {
    //     this.handleFetchArticle();

    // }
    componentDidMount() {

        try {
            this.handleFetchArticle();
            const json = localStorage.getItem('article');
            const article = JSON.parse(json);


            if (article) {
                this.setState(() => ({ article }));
            } 
        } catch (error) { }
    }
    componentDidUpdate(prevProps, prevState) {
        if (prevState.article.length !== this.state.article.length) {
            this.handleFetchArticle();
            const json = JSON.stringify(this.state.article);
            localStorage.setItem('article', json);
            console.log(this.state, 'componeent did update')
        }
    }

    handleToggleComments = () => {
        this.setState((prevState) => ({
            toggleComments: !prevState.toggleComments
        }))
    }
    handleFetchArticle = () => {
        api.fetchArticle(this.props.articleId)
        .then(article => {
            console.log(article.article)
            const singleArticle = article.article;
            this.setState({
                article: singleArticle.body,
                isLoading: false,
            }, () => { console.log(this.state, 'state after call')})
        })
    }
}

export default Article;
