import React, { Component } from 'react';
import { Link } from '@reach/router';
import Comments from './Comments';
import * as api from '../Utils/fetchData';
import Loading from './Loading';

class Article extends Component {

    state = {
        toggleComments: false,
        article: '',
        isLoading: true
    }
    render() {
        const { article } = this.state.article;
        const { isLoading } = this.state;

        return (
            <div>
            {console.log(article)}
                if (isLoading) return <Loading />
                <p>Article Data here</p>
                <button type="submit">
                    <Link to="/articles">Back</Link>
                </button>
                <button onClick={this.handleToggleComments} type="submit">comments</button>
                {this.state.toggleComments && <Comments />}

            </div>
        );
    }
    componentDidMount = (prevProps, prevState) => {
        this.handleFetchArticle();
    }

    handleToggleComments = () => {
        this.setState((prevState) => ({
            toggleComments: !prevState.toggleComments
        }))
    }
    handleFetchArticle = () => {
        api.fetchArticle(this.props.articleId)
        .then(article => {
            this.setState({
                article,
                isLoading: false
            }, () => {
                console.log(this.state)
            })
        })
    }
}

export default Article;
