import React, { Component } from 'react';
import { Link } from '@reach/router';
import * as api from '../Utils/fetchData';
import Loader from './Loader';
import formatDate from '../Utils/utilFunctions';
import AddArticle from './AddArticle';
import throttle from 'lodash.throttle';


class AllArticles extends Component {
  state = {
    slug: '',
    articles: [],
    isLoading: true,
    value: '',
    search: '',
    page: 1,
    hasAllArticles: false

  };
  render() {

    const { isLoading } = this.state;
    let { articles } = this.state;
    // articles.forEach((article, index) => {
    //   const firstIndex = articles.findIndex(firstarticle => article.article_id === firstarticle.article_id) 
    //   if (firstIndex !== index) {
    //     console.log(article)
    //   }
    // })
    const { username } = this.props.user
    if (isLoading)
      return <Loader type="Bars" color="#somecolor" height={80} width={80} />;
    return (

      <div className="articleList">

        <AddArticle fetchNewArticle={this.fetchNewAticle} user={this.props.user} />
        <form onSubmit={this.handleSubmit}>
          <select onChange={this.handleChange}>
            <option value="created_at">
              date first
            </option>
            <option value="created_at&sort_ascending=true">
              date last
            </option>
            <option value="votes&sort_ascending=true">votes</option>
            <option value="comment_count">most comments</option>
          </select>
        </form>
        {articles.map(
          ({ article_id, title, topic, author, created_at }) => {
            return (
              <li key={article_id}>
                <p>Title: {title}</p>
                <p>Topic: {topic}</p>
                <p>Author: {username}</p>
                <p>Date: {formatDate(created_at)}</p>

                <Link
                  className="buttonViewArticles"
                  to={`/articles/${article_id}`}
                >
                  View Article
                </Link>
              </li>
            );
          }
        )}
      </div>
    );
  }

  componentDidMount = () => {
    window.addEventListener('scroll', this.handleScroll)
    const { slug } = this.props;
    console.log(slug)
    if (slug) {   
      this.fetchArticlesByTopic() 
    } else {
      this.fetchArticles();

    }

  };
  componentDidUpdate = (prevProps, prevState) => {    
    const pageUpdate = prevState.page !== this.state.page;
    const topicUpdate = prevProps.slug !== this.props.slug;
    if (prevState.value !== this.state.value && this.state.value !== '') {
      this.fetchSortedArticles()   
    }
    if (pageUpdate && !this.state.hasAllArticles) {
      const { slug } = this.props;
      if (slug) {
        this.fetchArticlesByTopic()

      } else {
        this.fetchArticles();

      }
    }
    if (topicUpdate) {
      this.resetToFirstPage()
    }

  };


  handleScroll = throttle(() => {
    const distanceFromTop = window.scrollY;
    const heightOfScreen = window.innerHeight;
    const fullDocumentHeight = document.body.scrollHeight;

    if (distanceFromTop + heightOfScreen > fullDocumentHeight - 100) {
      this.setState(({ page }) => ({
        page: page + 1
      }))
    }
  }, 1000) 

  fetchArticles = () => {
    const { page } = this.state;
    api.fetchAllArticles(page).then(newArticles => {
      this.setState(({ articles }) => ({
        articles: page === 1 ? newArticles : [...articles, ...newArticles],
        isLoading: false
        }));
        if (!newArticles.length) 
        this.setState({
          hasAllArticles: true,
          isLoading: false
        })
    })
    .catch(err => {
      this.setState({
        hasAllArticles: true,
        isLoading: false
      })
    })
  };


  fetchArticlesByTopic = () => {
    const { page } = this.state;
    const { slug } = this.props;
    api.fetchArticlesByTopic(slug, page)
    .then(newArticles => {
      this.setState(({ articles }) => ({
        articles: page === 1 ? newArticles : [...articles, ...newArticles],
        isLoading: false
      }));
      if (!newArticles.length)
        this.setState({
          hasAllArticles: true,
          isLoading: false
        })
    })
      .catch(err => {
        this.setState({
          hasAllArticles: true,
          isLoading: false
        })
      })
  };

  fetchSortedArticles = () => {
    const { page } = this.state;
    const { value } = this.state;
    api.fetchArticlesSort(value, page)
    .then(() => this.resetToFirstPage())
    .then(newArticles => {
      this.setState(({articles}) => ({
        articles: page === 1 ? newArticles : [...articles, ...newArticles],
        isLoading: false
    }));
    if (!newArticles.length)
      this.setState({
        hasAllArticles: true,
        isLoading: false
      })
  })
      .catch(err => {
    this.setState({
      hasAllArticles: true,
      isLoading: false
    })
    });
  }

  handleChange = event => {
    this.setState({
      value: event.target.value
    });
  };
  handleSubmit = event => {
    event.preventDefault();
    this.setState({
      value: ''
    });
  };
  fetchNewAticle = (article) => {
    this.setState((prevState) => ({
      articles: [article, ...prevState.articles]
    }))
  }
  resetToFirstPage = () => {
    this.setState({
      page: 1,
      hasAllArticles: false
    })
  }
}

export default AllArticles;
