import React, { Component } from 'react';
import * as api from '../Utils/fetchData';

class RecentArticles extends Component {
    state = {
        articles: [],
        limit: 3,
        sort_by: 'created_at',
        page: 1
    }
    render() {
        return (
            <div>
                <h1>Check out our latest articles</h1>
            </div>
        );
    }
    componentDidMount = () => {

    }
    mostRecent() {
        const { limit, sort_by, page } = this.state;
        api.fetchArticles(sort_by, page, limit)
            .then(articles => this.setState({
                articles
        }), () => {
            console.log(this.state)
        })

    }
}

export default RecentArticles;
