import React, { Component } from 'react';
import AllArticles from './AllArticles';
import * as api from '../Utils/fetchData';





class Articles extends Component {
    state = {
        articles: []
    }
    render() {
        return (
            <div>
                <h1>Articles</h1>
                <AllArticles />
            </div>
        );
    }
    fetchArticles = () => {
        api.fetchAllArticles()
        .then(articles => {
            this.setState({
                articles
            })
        })
    }
}

export default Articles;
