import React, { Component } from 'react';
import { Link } from '@reach/router';
import * as api from '../Utils/fetchData';
import Loader from './Loader';
import formatDate from '../Utils/utilFunctions';
import AddArticle from './AddArticle';
import throttle from 'lodash.throttle';


class AllArticles extends Component {
  state = {
    articles: [],
    isLoading: true,
    value: '',
    search: '',
    page: 1,
    hasAllArticles: false,
    users: [
      {
        "username": "tickle122",
        "avatar_url": "https://avataaars.io/?avatarStyle=Circle&topType=ShortHairDreads02&accessoriesType=Wayfarers&hairColor=Black&facialHairType=Blank&facialHairColor=Blonde&clotheType=BlazerShirt&clotheColor=PastelOrange&graphicType=Diamond&eyeType=Happy&eyebrowType=SadConcerned&mouthType=Concerned&skinColor=Pale",
        "name": "Tom Tickle"
      },
      {
        "username": "grumpy19",
        "avatar_url": "https://avataaars.io/?avatarStyle=Circle&topType=NoHair&accessoriesType=Prescription01&hairColor=BlondeGolden&facialHairType=BeardMedium&facialHairColor=Platinum&clotheType=Hoodie&clotheColor=Blue01&eyeType=Squint&eyebrowType=UpDownNatural&mouthType=Smile&skinColor=Light",
        "name": "Paul Grump"
      },
      {
        "username": "happyamy2016",
        "avatar_url": "https://avataaars.io/?avatarStyle=Circle&topType=LongHairStraightStrand&accessoriesType=Prescription01&hairColor=Auburn&facialHairType=Blank&clotheType=Hoodie&clotheColor=PastelRed&eyeType=Surprised&eyebrowType=UnibrowNatural&mouthType=Tongue&skinColor=Tanned",
        "name": "Amy Happy"
      },
      {
        "username": "cooljmessy",
        "avatar_url": "https://avataaars.io/?avatarStyle=Circle&topType=ShortHairShortFlat&accessoriesType=Blank&hairColor=Black&facialHairType=Blank&clotheType=ShirtCrewNeck&clotheColor=PastelBlue&eyeType=Default&eyebrowType=UpDownNatural&mouthType=Grimace&skinColor=Black",
        "name": "Peter Messy"
      },
      {
        "username": "weegembump",
        "avatar_url": "https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription01&hairColor=Auburn&facialHairType=Blank&clotheType=ShirtScoopNeck&clotheColor=PastelOrange&eyeType=Surprised&eyebrowType=RaisedExcited&mouthType=Grimace&skinColor=Brown",
        "name": "Gemma Bump"
      },
      {
        "username": "jessjelly",
        "avatar_url": "https://avataaars.io/?avatarStyle=Circle&topType=LongHairBob&accessoriesType=Round&hairColor=Black&facialHairType=Blank&facialHairColor=BlondeGolden&clotheType=Overall&clotheColor=Gray02&eyeType=Squint&eyebrowType=RaisedExcitedNatural&mouthType=Eating&skinColor=Light",
        "name": "Jess Jelly"
      }
    ]

  };
  render() {
    const { isLoading } = this.state;
    let { articles } = this.state;




    if (isLoading)
      return <Loader type="Bars" color="#somecolor" height={80} width={80} />;
    return (
      <div>
        <h2 className="articleHeading">Articles</h2>
        <div className="articleList">

          <AddArticle
            fetchNewArticle={this.fetchNewAticle}
            user={this.props.user}
          />
          <form onSubmit={this.handleSubmit}>
            <p id="sortLabel">Sort</p>
          <div className="categories" id="sortbySelect">

              <select className="select" onChange={this.handleChange}>
                <option />
                <option value="created_at">date first</option>
                <option value="created_at&sort_ascending=true">date last</option>
                <option value="votes&sort_ascending=true">votes</option>
                <option value="comment_count">most comments</option>
              </select>
          </div>

          </form>

          {articles.map(({ article_id, title, topic, author, created_at }) => {
            const userObject = this.state.users.filter(user => user.username === author)
            console.log(userObject.avatar_url)
            return <li key={article_id}>

                <h3 className="articleTitle">{title}</h3>

                <p className="articleP">Topic: {topic}</p>
                <img src={userObject.avatar_url} alt="avatar"></img>
                <p className="articleP">Author: {author}</p>
                <p className="articleP">Date: {formatDate(created_at)}</p>

                <Link className="buttonViewArticles" to={`/articles/${article_id}`}>
                  View Article
                </Link>
              </li>;
          })}
        </div>
      </div>

    );
  }

  componentDidMount = () => {

    window.addEventListener('scroll', this.handleScroll);
    this.handleFetchArticles();

  };

  componentDidUpdate = (prevProps, prevState) => {

    const pageUpdate = prevState.page !== this.state.page;
    const topicUpdate = prevProps.slug !== this.props.slug;
    if (prevState.value !== this.state.value && this.state.value !== '') {
          this.resetToFirstPage();         
    }
    if (pageUpdate && !this.state.hasAllArticles) {
      this.handleFetchArticles();
    }
    if (topicUpdate) {
      this.resetToFirstPage();
    }

  };



  handleScroll = throttle(() => {
    const distanceFromTop = window.scrollY;
    const heightOfScreen = window.innerHeight;
    const fullDocumentHeight = document.body.scrollHeight;

    if (distanceFromTop + heightOfScreen > fullDocumentHeight - 100) {
      this.setState(({ page }) => ({
        page: page + 1
      }));
    }
  }, 1000);

  handleFetchArticles = () => {
    const { page, value } = this.state;
    const { slug } = this.props;
    api
      .fetchArticles(slug, value, page)
      .then(newArticles => {
        this.setState(({ articles }) => ({
          articles: page === 1 ? newArticles : [...articles, ...newArticles],
          isLoading: false
        }));
        if (!newArticles.length)
          this.setState({
            hasAllArticles: true,
            isLoading: false
          });
      })
      .catch(err => {
        this.setState({
          hasAllArticles: true,
          isLoading: false
        });
      });
  };

  handleChange = event => {
    this.setState({
      value: event.target.value
    });
  };
  handleSubmit = event => {
    event.preventDefault();
    this.setState({
      value: ''
    });
  };
  fetchNewAticle = article => {
    console.log(article, 'article here')
    this.setState(prevState => ({

      articles: [article, ...prevState.articles]
    }), ()=>console.log(this.state, 'NEW ARTICLE LIST'));
  };
  resetToFirstPage = () => {
    this.setState({
      page: 1,
      hasAllArticles: false
    }, this.handleFetchArticles);
  };

}

export default AllArticles;
