import React, { Component } from 'react';
import formatDate from '../Utils/utilFunctions';
import Voter from './Voter';
import AddComment from './AddComment';
import * as api from '../Utils/fetchData';
import throttle from 'lodash.throttle';
import Err from './Err';

class Comments extends Component {
  _isMounted = false;
  state = {
    comments: [],
    page: 1,
    sort_by: 'created_at',
    hasAllComments: false,
    limit: 10,
    hasError: false,
    error: ''
  };
  render() {
    const { comments } = this.state;
    const { articleid, user } = this.props;
    const { hasError, error } = this.state;
    if (hasError) return <Err error={error} />;

    return (
      <div className="commentsDiv">
        <AddComment
          articleid={articleid}
          getComment={this.getComment}
          user={user}
        />
        <ul>
          <div className="commentList">
            {comments.map(comment => (
              <li key={comment.comment_id}>
                <p>{comment.body}</p>
                <p>published by: {comment.author}</p>
                <p>left: {formatDate(comment.created_at)}</p>
                <Voter
                  votes={comment.votes}
                  articleid={articleid}
                  commentid={comment.comment_id}
                  parent="comments"
                />
                <button
                  type="submit"
                  onClick={() =>
                    this.handleDeleteComment(articleid, comment.comment_id)
                  }
                  disabled={user.username !== comment.author}
                >
                  Delete
                </button>
              </li>
            ))}
          </div>
        </ul>
      </div>
    );
  }
  componentDidMount = () => {
    this._isMounted = true;
    window.addEventListener('scroll', this.handleScroll);
    this.handleFetchComments();
  };

  componentDidUpdate = (prevProps, prevState) => {
    const { page, hasAllComments } = this.state;
    const pageUpdate = prevState.page !== page;
    if (pageUpdate && !hasAllComments) {
      this.handleFetchComments();
    }
  };

  componentWillUnmount() {
    this._isMounted = false;
  }

  handleScroll = throttle(() => {
    const distanceFromTop = window.scrollY;
    const heightOfScreen = window.innerHeight;
    const fullDocumentHeight = document.body.scrollHeight;

    if (distanceFromTop + heightOfScreen > fullDocumentHeight - 100) {
      if (this._isMounted)
        this.setState(({ page }) => ({
          page: page + 1
        }));
    }
  }, 1000);

  handleFetchComments = () => {
    const { articleid } = this.props;
    const { sort_by, page, limit } = this.state;
    api
      .fetchComments(articleid, sort_by, page, limit)
      .then(newComments => {
        this.setState(({ comments }) => ({
          comments: page === 1 ? newComments : [...comments, ...newComments]
        }));
        if (!newComments.length)
          this.setState({
            hasAllComments: true
          });
      })
      .catch(err => {
        this.setState({
          hasAllComments: true
        });
      });
  };
  getComment = comment => {
    this.setState(prevState => {
      return { comments: [comment, ...prevState.comments] };
    });
  };

  deleteComment = commentid => {
    this.setState(prevState => ({
      comments: prevState.comments.filter(
        ({ comment_id }) => comment_id !== commentid
      )
    }));
  };

  handleDeleteComment = (articleid, commentid) => {
    api
      .deleteData(articleid, commentid)
      .then(res => {
        this.deleteComment(commentid);
      })
      .catch(err => {
        this.setState({ hasError: true, error: err });
      });
  };
}

export default Comments;
