import React, { Component } from 'react';
import { Link, Router } from '@reach/router';
import Comments from './Comments';

class Arcticle extends Component {
    state = {
        showComments: false
    }
    render() {
        return (
            <div>
                <h1>Individual Article</h1>
                <p>Article Data here</p>
                <button type="submit">
                    <Link to="/articles">Back</Link>
                </button>
                <button onClick={this.handleToggleComments} type="submit">comments</button>
                {!this.state.handleToggleComments && <Comments />}

            </div>
        );
    }
    handleToggleComments = () => {
        this.setState((prevState) => ({
            showComments: !prevState.showComments
        }))
    }
}

export default Arcticle;
