import React, { Component } from 'react';


class Topics extends Component {
    state = { 
        slug: '',
        description: '', 
    }
    render() {
        return (

                <div>
                    <h1>Topics</h1>
                    <form onSubmit={this.onSubmit}>
                        <label htmlFor="slug">Slug</label>
                        <input type="text" value={this.state.slug} onChange={this.handleChange} id="slug"></input>
                        <label htmlFor="description">Description</label>
                        <input type="text" value={this.state.description} onChange={this.handleChange} id="description"></input>
                        <button type="submit">Add Topic</button>
                    </form>

                </div>

        );
    }
    handleChange = (event) => {
        const { value, id } = event.target;
        this.setState({
            [id]: value
        }, console.log(this.state))
    }
    onSubmit = (event) => {
        event.preventDefault();
        this.addTopicCall();
        this.setState({
            slug: '',
            description: ''
        })
    }

    addTopicCall = () => {
        const { slug, description } = this.state;
        return fetch(`https://lloyd-news.herokuapp.com/api/topics`, {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                slug: slug,
                description: description
            })
        }).then((res) => {
            res.json()
        }).then(({topic}) => {
            console.log(topic)
        })
    }
}

export default Topics;
