import React, { Component } from 'react';
import { Link } from '@reach/router';
import Comments from './Comments';
import * as api from '../Utils/fetchData';
import Loader from './Loader';

class Article extends Component {

    state = {
        toggleComments: false,
        article: [],
        isLoading: true,
        body: ''
    }
    render() {

        const { isLoading } = this.state;

        if (isLoading) return <Loader />;
        console.log(this.state)
        return <div>
            <p>{this.state.article.body}</p>
            {console.log(this.props)}
            <button type="submit">
              <Link to="/articles">Back</Link>
            </button>
            <button onClick={this.handleToggleComments} type="submit">
              comments
            </button>
            {this.state.toggleComments && <Comments />}
          </div>;
    }
    // componentDidMount = () => {
    //     this.handleFetchArticle();

    // }
    componentDidMount() {
       this.handleFetchArticle();  
    }


    handleToggleComments = () => {
        this.setState((prevState) => ({
            toggleComments: !prevState.toggleComments
        }))
    }
    handleFetchArticle = () => {
        const { articleid } = this.props;
        api.fetchArticle(articleid)
        .then(article => {
            const singleArticle = article.article;
            this.setState({
                article: singleArticle.body,
                isLoading: false,
            }, () => { console.log(this.state, 'state after call')})
        })
    }
}

export default Article;
